import React, { useState } from 'react'
import { StyleSheet, Text, View } from 'react-native'
import Card from '../../../components/Card'
import { CardType, SubscriptionData } from '../../../components/types'
import {Surface} from 'react-native-paper'
import { Button } from 'react-native-elements'

const PaySubscribe = () => {
    const [subscribeData, setSubscribeData] = useState<SubscriptionData>()
    const [paymentType, setPaymentType] = useState('manual')
    return (
        <View>
            <Card title="몇명이 사용하시나요" data={subscribeData} type={CardType.CHARGE}/>
            <Card title="결제주기를 선택해주세요" subtitle="멤버 1명 당 결제금액" data={subscribeData} type={CardType.CHARGERENDER}/>
            <Card title="총 결제하실 금액(부가세 포함)" subtitle="2021년 1월 1일 ~ 2022년 12월 31일" data={subscribeData} type={CardType.CHARGERENDER}/>
            <View style={{flexDirection:'row', justifyContent:'space-between'}}>
                <Surface style={styles.surface}>
                    <Button 
                    title = "수동결제" 
                    type="clear" 
                    titleStyle={styles.buttonTitleFont}
                    buttonStyle={{backgroundColor: paymentType === 'manual' ? "#FB8C00" : "#ADB2BA", width: 130}}
                    onPress={()=>{setPaymentType('manual')}}
                    />
                    <Text>매월 청구된 금액을 수동으로 직접 결제</Text>
                </Surface>
                <Surface style={styles.surface}>
                    <Button 
                    title = "자동결제"
                    type="clear" 
                    titleStyle={styles.buttonTitleFont}
                    buttonStyle={{backgroundColor: paymentType === 'auto' ? "#FB8C00" : "#ADB2BA", width: 130}}
                    onPress={()=>{setPaymentType('auto')}}
                    />
                    <Text>매월 청구된 금액을 지정된 결제방식으로 자동결제</Text>
                </Surface>
            </View>
        </View>
    )
}

export default PaySubscribe

const styles = StyleSheet.create({
    surface: {
        padding: 8,
        width: 260,
        height: 200,
        alignItems: 'center',
        justifyContent: 'center',
        elevation: 8,
        borderRadius:8
      },
    buttonTitleFont:{
        fontWeight: '700',
        fontSize: 14,
        lineHeight: 16,
        letterSpacing:0.5,
        color:'white'
    }
})
