## Mutation  :
mutation {
  createSurvey (input:
    {
      data: {
        title: "  ",
        description: "       .            .",
        isTemplate: true,
        option: {
          __typename: "ComponentActivityTemplateOption",
          scope: SYSTEM,
          availableIn: ""
        },
        questions: []
        activity: null,
        template: null,
        workspace: 1
      }
    }
  )
  {
    survey {
      ...surveyFields
    }
  }
}

#:   surveyFields fragment  .
fragment surveyFields on Survey {
  id
  title
  description
  option {
    ... on ComponentActivitySurveyOption {
      resubmit
    }
    ... on ComponentActivityTemplateOption {
      scope
      availableIn
    }
  }
  questions {
    ...questionFields
  }
  workspace {
    id
  }
  activity {
    id
  }
}

fragment questionFields on Question {
  id
  type
  title
  description
  content {
    ... on ComponentActivityScaleOption {
      type
      min
      max
      customLabel {
        key
        label
      }
    }
    ... on ComponentActivityTextOption {
      characterLimit
    }
    ... on ComponentActivityChoiceOptionList {
      item {
        sequence
        label
        text
        image {
          url
        }
        value
      }
    }
  }
  tags
}
